import React,{ useState } from "react";
import Button from "react-bootstrap/Button";
import Form from"react-bootstrap/Form";
import {addUser, newUser} from "../Actions/usersAction";
import {connect,useDispatch} from "react-redux";
import {v4 as uuid} from "uuid";
import { collection, addDoc } from "firebase/firestore"; 
import {db} from '../Firebase/Config';



 function AddUsersForm(props) {
    const [username,setUsername]= useState(" ");
    const [email,setEmail]= useState(" ");
    const [mobile,setMobile]= useState(" ");
    const [password,setPassword]= useState(" ");

   const dispatch = useDispatch();
    const handleSubmit = async(e) => { 
     // Add a new document with a generated id.

// console.log("Document written with ID: ", docRef.id);


      e.preventDefault();
      // props.newUser({username,email,mobile,password});
      let userInfo = {id:uuid(), username,email,mobile,password};
      dispatch(addUser(userInfo));
      const docRef = await addDoc(collection(db, "Users_TB",userInfo.id),userInfo);       
      setUsername("");
      setEmail("");
      setMobile("");
      setPassword("");
  };

  return (
    <Form>

      <Form.Group className="" controlId="formBasicEmail">
        <Form.Label>Username</Form.Label>
        <Form.Control type="username"
         placeholder="Enter your username"
         value={username} 
         onChange={(e) => {
            setUsername(e.target.value)
         }}
         />
      </Form.Group>

      <Form.Group className="mb-3" controlId="formBasicEmail">
        <Form.Label>Email</Form.Label>
        <Form.Control type="email"
         placeholder="Enter your email"
         value={email} 
         onChange={(e) => {
            setEmail(e.target.value)
         }}
         />
      </Form.Group>


      <Form.Group className="mb-3" controlId="formBasicEmail">
        <Form.Label>Mobile</Form.Label>
        <Form.Control type="mobile"
         placeholder="Enter your mobile number"
         value={mobile} 
         onChange={(e) => {
            setMobile(e.target.value)
         }}
         />
      </Form.Group>

      <Form.Group className="mb-3" controlId="formBasicEmail">
        <Form.Label>Password</Form.Label>
        <Form.Control type="password"
         placeholder="Enter your password"
         value={password} 
         onChange={(e) => {
            setPassword(e.target.value)
         }}
         />
      </Form.Group>


      <Button onClick={handleSubmit} variant="primary" type="submit">Submit</Button>
    </Form>

  );
}
export default AddUsersForm;
